import React from 'react';
import './D2CMainPage.css';

// 제목과 부제목, 섹션 제목들을 상수로 분리하여 정의하고 내보냅니다
export const PAGE_TITLE = "DX Automation for D2C";
export const PAGE_SUBTITLE = "D2C 매출 현황 및 원인 분석 도구";

// 섹션 제목들도 상수로 분리
export const SECTION_TITLES = {
  SERVICE_OVERVIEW: "서비스 개요",
  D2C_STRATEGY: "효과적인 D2C 전략",
  START_NOW: "지금 시작해보세요"
};

// 서비스 설명을 상수로 분리
export const SERVICE_DESCRIPTION = 
  "해외법인에서 운영하는 OBS (Online Brand Shop)에서 수집되는 매출 및 고객웹행동 데이터를 기반으로 " +
  "매출현황과 판매량을 법인전체/제품군/모델 단위로 파악하고, 매출하락 등의 이슈에 대한 원인을 OBS Funnel 단계 " +
  "및 OBS 유입 채널 관점에서 파악하고 해결방안을 제시합니다.";

// 통계 데이터를 상수로 분리
export const STATS_DATA = [
  { value: "42%", label: "고객 유지율 증가" },
  { value: "3.5x", label: "ROI 향상" },
  { value: "67%", label: "마케팅 자동화 효율" }
];

// 데모 섹션 텍스트를 상수로 분리
export const DEMO_SECTION = {
  DESCRIPTION: "DX Automation for D2C 솔루션으로 고객 중심의 비즈니스 혁신을 경험해보세요.",
  BUTTON_TEXT: "데모 요청하기"
};

// 서비스 기능 카드 데이터를 상수로 분리
export const FEATURE_CARDS = [
  {
    icon: "📊",
    title: "고객 데이터 통합",
    description: "다양한 소스의 고객 데이터를 통합하여 단일 뷰에서 관리할 수 있습니다."
  },
  {
    icon: "🔄",
    title: "자동화 워크플로우",
    description: "복잡한 고객 여정을 자동화하여 효율적인 마케팅 운영이 가능합니다."
  },
  {
    icon: "📱",
    title: "옴니채널 경험",
    description: "모든 접점에서 일관된 고객 경험을 제공하도록 설계되었습니다."
  },
  {
    icon: "🧠",
    title: "AI 기반 분석",
    description: "고급 AI 알고리즘을 활용하여 고객 행동을 예측하고 인사이트를 도출합니다."
  }
];

const D2CMainPage = () => {
  // 기능 카드 표시 여부 (필요시 true로 변경)
  const showFeatureCards = false;

  return (
    <div className="d2c-main-page">
      <div className="page-header">
        <h1>{PAGE_TITLE}</h1>
        <p className="subtitle">{PAGE_SUBTITLE}</p>
      </div>

      <div className="feature-section">
        <h2>{SECTION_TITLES.SERVICE_OVERVIEW}</h2>
        <div className="service-description-container">
          <p className="service-description">{SERVICE_DESCRIPTION}</p>
        </div>
        <div className="feature-grid">
          {showFeatureCards && FEATURE_CARDS.map((card, index) => (
            <div className="feature-card" key={index}>
              <div className="icon">{card.icon}</div>
              <h3>{card.title}</h3>
              <p>{card.description}</p>
            </div>
          ))}
        </div>
      </div>

      <div className="stats-section">
        <h2>{SECTION_TITLES.D2C_STRATEGY}</h2>
        <div className="stats-container">
          {STATS_DATA.map((stat, index) => (
            <div className="stat-item" key={index}>
              <span className="stat-value">{stat.value}</span>
              <span className="stat-label">{stat.label}</span>
            </div>
          ))}
        </div>
      </div>

      <div className="demo-section">
        <h2>{SECTION_TITLES.START_NOW}</h2>
        <p>{DEMO_SECTION.DESCRIPTION}</p>
        <button className="demo-button">{DEMO_SECTION.BUTTON_TEXT}</button>
      </div>
    </div>
  );
};

export default D2CMainPage;
