import React, { useState } from 'react';
import Header from './components/Layout/Header';
import Sidebar from './components/Layout/Sidebar';
import ChatInterface from './components/Chat/ChatInterface';
import ChatInterface_2 from './components/Chat/ChatInterface_2';
import D2CMainPage from './components/D2C/D2CMainPage';
import './styles/App.css';

// 하위 메뉴 페이지 컴포넌트들
const WorkspacePage = () => (
  <div className="submenu-page">
    <h2>D2C Workspace</h2>
    <p>이 페이지에서는 D2C 분석을 위한 작업 환경을 설정할 수 있습니다.</p>
    <div className="workspace-content">
      <div className="card">
        <h3>프로젝트 관리</h3>
        <p>데이터 분석 프로젝트를 생성하고 관리할 수 있습니다.</p>
      </div>
      <div className="card">
        <h3>팀 협업</h3>
        <p>팀원들과 협업하여 분석 결과를 공유할 수 있습니다.</p>
      </div>
    </div>
  </div>
);

const AccountPage = () => (
  <div className="submenu-page">
    <h2>D2C 계정 설정</h2>
    <p>계정 정보 및 권한을 관리할 수 있습니다.</p>
    <div className="account-content">
      <div className="card">
        <h3>프로필 설정</h3>
        <p>사용자 프로필 정보를 업데이트할 수 있습니다.</p>
      </div>
      <div className="card">
        <h3>알림 설정</h3>
        <p>알림 및 이메일 수신 설정을 관리할 수 있습니다.</p>
      </div>
    </div>
  </div>
);

function App() {
  const [activePage, setActivePage] = useState('page1');

  const getCurrentPageComponent = () => {
    switch(activePage) {
      case 'page1':
        return <ChatInterface serviceId="page1" />;
      case 'page2':
        return <D2CMainPage />;
      case 'page2-workspace':
        return <WorkspacePage />;
      case 'page2-account':
        return <AccountPage />;
      default:
        return <ChatInterface serviceId="page1" />;
    }
  };

  return (
    <div className="app">
      <Header />
      <div className="content">
        {/* Sidebar 컴포넌트에서 page2 선택시 자체적으로 내용 표시 */}
        <Sidebar
          serviceId={activePage}
          onSelectPage={setActivePage}
          activePage={activePage}
        />

        <main className="main-content">
          {getCurrentPageComponent()}
        </main>
      </div>
    </div>
  );
}

export default App;
