import React from 'react';

const ChatMessage = ({ role, content, isError = false, metadata }) => {
  // Ïó≠Ìï†Ïóê Îî∞Î•∏ ÌÅ¥ÎûòÏä§ Í≤∞Ï†ï
  const messageClass = `chat-message ${role} ${isError ? 'error' : ''}`;
  
  // Ïó≠Ìï†Ïóê Îî∞Î•∏ ÏïÑÎ∞îÌÉÄÏôÄ Ïù¥Î¶Ñ Í≤∞Ï†ï
  const avatar = role === 'user' ? 'üë§' : 'ü§ñ';
  const name = role === 'user' ? 'ÏÇ¨Ïö©Ïûê' : 'Intellytics AI';
  
  // sourcesÍ∞Ä ÏûàÎäîÏßÄ ÌôïÏù∏
  const hasSources = metadata && metadata.sources && metadata.sources.length > 0;
  
  return (
    <div className={messageClass}>
      <div className="message-avatar">{avatar}</div>
      <div className="message-content">
        <div className="message-header">
          <span className="message-name">{name}</span>
        </div>
        <div className="message-text">
          {content}
        </div>
        
        {/* B2B Query ÏÜåÏä§ Ï†ïÎ≥¥ ÌëúÏãú ÏòÅÏó≠ */}
        {hasSources && (
          <div className="message-sources">
            <div className="sources-header">Ï∞∏Í≥† Ï†ïÎ≥¥:</div>
            <ul className="sources-list">
              {metadata.sources.map((source, index) => (
                <li key={index} className="source-item">
                  {typeof source === 'string' 
                    ? source 
                    : JSON.stringify(source)
                  }
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
};

export default ChatMessage;
