import React from 'react';
import useChat from '../../hooks/useChat';
import ChatMessage from './ChatMessage';
import ChatInput from './ChatInput';
import SampleQuestions from './SampleQuestions';
import Spinner from '../UI/Spinner';

// 샘플 페이지별 사이드바 내용
export const SidebarPage1Extra = () => (
  <div>
    <h3>Intellytics 분석 가이드</h3>
    <ul>
      <li>NPS 점수 해석법</li>
      <li>VOC 클러스터링 예시</li>
      <li>분석 리포트 다운로드</li>
    </ul>
  </div>
);

export const SidebarPage2Extra = () => (
  <div className="sidebar-page2-extra">
    <h3>D2C 분석 팁</h3>
    <ul>
      <li>이탈 고객 패턴 분석</li>
      <li>프로모션 효과 분석</li>
      <li>고객 세그먼트 전략</li>
    </ul>
    <div className="page2-extra-info">
      <h4>D2C 분석 방법론</h4>
      <p>이 섹션은 page2가 선택되었을 때만 표시됩니다</p>
      <p>D2C 분석은 고객 세그먼트를 정의하고 각 세그먼트별로 전략을 수립하는 데 도움이 됩니다.</p>
    </div>
  </div>
);

const ChatInterface = ({ serviceId, SidebarContent }) => {
  const SERVICE_NAME = "sample A";
  const SERVICE_DESCRIPTION = "AI와 대화하며 다양한 질문에 대한 답변을 받아보세요.";

  const SAMPLE_QUESTIONS = [
    "이 서비스는 어떤 기능을 제공하나요?",
    "NPS 분석이란 무엇인가요?",
    "VOC 데이터를 어떻게 분석할 수 있나요?",
    "D2C 분석에 대해 설명해주세요."
  ];

  const {
    messages,
    isLoading,
    chatContainerRef,
    bottomRef,
    sendMessage,
    handleSampleQuestion
  } = useChat(serviceId);

  return (
    <div className="chat-interface">
      {/* 상단 제목 */}
      <div className="service-header">
        <h2 className="main-title">{SERVICE_NAME}</h2>
        <p className="service-description">{SERVICE_DESCRIPTION}</p>
      </div>

      <div className="chat-main-area">
        {/* 💬 채팅 메시지 영역 */}
        <div className="chat-container" ref={chatContainerRef}>
          {(
            <SampleQuestions
              questions={SAMPLE_QUESTIONS}
              onSelectQuestion={handleSampleQuestion}
              isLoading={isLoading}
            />
          )}

          {messages.map((message, index) => (
            <ChatMessage
              key={index}
              role={message.role}
              content={message.content}
              isError={message.isError}
            />
          ))}

          {isLoading && (
            <div className="loading-message">
              <Spinner />
            </div>
          )}

          {/* 자동 스크롤 포인트 */}
          <div ref={bottomRef} />
        </div>

        {/* 🔧 각 페이지 전용 사이드바 내용 (선택적 렌더링) */}
        {SidebarContent && (
          <div className="chat-sidebar-extra">
            {SidebarContent}
          </div>
        )}
      </div>

      {/* 🧾 입력창: 항상 하단 고정 */}
      <div className="chat-input-container">
        <ChatInput
          onSendMessage={sendMessage}
          isLoading={isLoading}
        />
      </div>
    </div>
  );
};

export default ChatInterface;
