import React from 'react';
import useChat from '../../hooks/useChat';
import ChatMessage from './ChatMessage';
import ChatInput from './ChatInput';
import SampleQuestions from './SampleQuestions';
import Spinner from '../UI/Spinner';

const ChatInterface = ({ serviceId }) => {
  // 서비스 정보
  const SERVICE_NAME = "Intellytics AI 대화 서비스";
  const SERVICE_DESCRIPTION = "AI와 대화하며 다양한 질문에 대한 답변을 받아보세요.";
  
  // 대표 질문 목록
  const SAMPLE_QUESTIONS = [
    "이 서비스는 어떤 기능을 제공하나요?",
    "NPS 분석이란 무엇인가요?",
    "VOC 데이터를 어떻게 분석할 수 있나요?",
    "D2C 분석에 대해 설명해주세요."
  ];
  
  // useChat 훅을 사용하여 채팅 기능 구현
  const {
    messages,
    isLoading,
    chatContainerRef,
    sendMessage,
    handleSampleQuestion
  } = useChat(serviceId);
  
  return (
    <div className="chat-interface">
      <div className="service-header">
        <h2 className="main-title">{SERVICE_NAME}</h2>
        <p className="service-description">{SERVICE_DESCRIPTION}</p>
      </div>
      
      {/* 메시지가 없으면 대표 질문 표시 */}
      {messages.length === 0 && (
        <SampleQuestions
          questions={SAMPLE_QUESTIONS}
          onSelectQuestion={handleSampleQuestion}
          isLoading={isLoading}
        />
      )}
      
      {/* 채팅 메시지 영역 */}
      <div className="chat-container" ref={chatContainerRef}>
        {messages.map((message, index) => (
          <ChatMessage
            key={index}
            role={message.role}
            content={message.content}
            isError={message.isError}
          />
        ))}
        
        {/* 로딩 중 스피너 */}
        {isLoading && (
          <div className="loading-message">
            <Spinner />
          </div>
        )}
      </div>
      
      {/* 채팅 입력 영역 */}
      <div className="chat-input-container">
        <ChatInput
          onSendMessage={sendMessage}
          isLoading={isLoading}
        />
      </div>
    </div>
  );
};

export default ChatInterface;
