import React from 'react';
import useChat from '../../hooks/useChat';
import ChatMessage from '../Chat/ChatMessage';
import ChatInput from '../Chat/ChatInput';
import SampleQuestions from '../Chat/SampleQuestions';
import Spinner from '../UI/Spinner';
import './D2CPages.css';

const WorkspacePage = ({ serviceId, SidebarContent }) => {
  const SERVICE_NAME = "D2C - Sales Status 서비스";
  
  const SERVICE_DESCRIPTION = {
    ko: `
<h3>서비스 개요</h3>
<p><strong>▶ D2C Sales Status 서비스</strong><br>
해외법인에서 운영하는 OBS (Online Brand Shop)에서 수집되는 매출 및 고객웹행동 데이터를 기반으로 매출현황과 판매량을 법인전체/제품군/모델 단위로 파악하고, 매출하락 등의 이슈에 대한 원인을 OBS Funnel 단계 및 OBS 유입 채널 관점에서 파악하고 해결방안을 제시합니다.</p>

<h3>데이터 설명</h3>
<p>해외법인 OBS에서 2021-01-01부터 현재까지 수집된 매출 및 웹행동 데이터</p>
<table>
    <colgroup>
        <col style="width: 16%">
        <col style="width: 4%">
        <col style="width: 40%">
        <col style="width: 40%">
    </colgroup>
    <thead>
        <tr>
            <td colspan="2">데이터 테이블</td>
            <td>데이터 Column</td>
            <td>설명</td>
        </tr>
    </thead>
    <tbody>
        <tr>
            <td rowspan="5">매출 데이터</td>
            <td>1</td>
            <td>Date</td>
            <td>판매일</td>
        </tr>
        <tr>
            <td>2</td>
            <td>Product Category</td>
            <td>제품군</td>
        </tr>
        <tr>
            <td>3</td>
            <td>Model</td>
            <td>모델명</td>
        </tr>
        <tr>
            <td>4</td>
            <td>Sales Amount</td>
            <td>매출액</td>
        </tr>
        <tr>
            <td>5</td>
            <td>Sales Quantity</td>
            <td>판매수량</td>
        </tr>
    </tbody>
</table>

<h3>주요 기능</h3>
<ul>
  <li>법인전체 매출현황/판매량 파악</li>
  <li>제품군별 매출현황/판매량 파악</li>
  <li>모델별 매출현황/판매량 파악</li>
  <li>기간 단위(일, 주, 월, 년)별 집계 및 비교</li>
  <li>OBS Funnel 단계 관점 원인 분석</li>
  <li>OBS 유입 채널 관점 원인 분석</li>
  <li>주요 원인 별 해결방안 제시</li>
</ul>
`,
    en: `under construction...`
  };

  const SAMPLE_QUESTIONS = [
    "지난 달 매출이 가장 높은 제품군 3개를 알려줘",
    "올해 2분기와 작년 2분기의 매출을 비교해줘",
    "어떤 제품의 매출이 지난 달 대비 가장 많이 감소했나요?"
  ];

  const {
    messages,
    isLoading,
    chatContainerRef,
    bottomRef,
    sendMessage,
    handleSampleQuestion
  } = useChat(serviceId || 'workspace');

  return (
    <div className="chat-interface">
      {/* 상단 제목 */}
      <div className="service-header">
        <h2 className="main-title">{SERVICE_NAME}</h2>
        <div 
          className="service-description" 
          dangerouslySetInnerHTML={{ __html: SERVICE_DESCRIPTION.ko }}
        />
      </div>

      <div className="chat-main-area">
        {/* 💬 채팅 메시지 영역 */}
        <div className="chat-container" ref={chatContainerRef}>
          {(
            <SampleQuestions
              questions={SAMPLE_QUESTIONS}
              onSelectQuestion={handleSampleQuestion}
              isLoading={isLoading}
            />
          )}

          {messages.map((message, index) => (
            <ChatMessage
              key={index}
              role={message.role}
              content={message.content}
              isError={message.isError}
            />
          ))}

          {isLoading && (
            <div className="loading-message">
              <Spinner />
            </div>
          )}

          {/* 자동 스크롤 포인트 */}
          <div ref={bottomRef} />
        </div>

        {/* 🔧 각 페이지 전용 사이드바 내용 (선택적 렌더링) */}
        {SidebarContent && (
          <div className="chat-sidebar-extra">
            {SidebarContent}
          </div>
        )}
      </div>

      {/* 🧾 입력창: 항상 하단 고정 */}
      <div className="chat-input-container">
        <ChatInput
          onSendMessage={sendMessage}
          isLoading={isLoading}
        />
      </div>
    </div>
  );
};

export default WorkspacePage;
